  //  val inputraw = "b inc 5 if a > 1\na inc 1 if b < 5\nc dec -10 if a >= 1\nc inc -20 if c == 10"
  var instructions = inputraw.split("\n").toList
    .map(_.split(" if "))
    .map(ar => (ar(0), ar(1)))

  val registers = scala.collection.mutable.Map[String, Int]()

  //  println(instructions)

  var highestRegValue = 0

  instructions.foreach(instruction => {
    println(instruction)

    var condition = instruction._2.split(" ")
    if (checkCondition(condition(0), condition(1), condition(2).toInt)) {

      val Array(reg, incdec, value) = instruction._1.split(" ")

      val currentRegValue = registers.get(reg).getOrElse(0)
      val newRegValue = if (incdec == "inc") {
        currentRegValue + value.toInt
      } else {
        currentRegValue - value.toInt
      }

      // keep record value, for part 2
      if (newRegValue > highestRegValue) highestRegValue = newRegValue

      registers.put(reg, newRegValue)
    }
  })

  def checkCondition(register: String, operator: String, compareValue: Int): Boolean = {
    operator match {
      case ">" => registers.getOrElse(register, 0) > compareValue
      case "<" => registers.getOrElse(register, 0) < compareValue
      case ">=" => registers.getOrElse(register, 0) >= compareValue
      case "==" => registers.getOrElse(register, 0) == compareValue
      case "<=" => registers.getOrElse(register, 0) <= compareValue
      case "!=" => registers.getOrElse(register, 0) != compareValue
      case _ => throw new RuntimeException
    }
  }

  //  println(checkCondition("a", "==", 5))
  //  registers.put("a",5)
  //  println(checkCondition("a", "==", 5))
  //  println(registers)
  // part 1
  println(registers.map(_._2).max)

  // part 2
  println(highestRegValue)
}
